package day2.localvariables;

public class Variable1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		byte num1 = 20;
		short num2 = 32;
		int num3 = -45;
		long num4 = 2467l;
		float num5 = 65.6f;
		double num6 = 2465.8764d;
		char grade = 'A';
		boolean pass = true;
		
		System.out.println("bytevariable=" + num1);
		System.out.println("shortvariable=" + num2);
		System.out.println("intvariable=" + num3);
		System.out.println("longvariable=" + num4);
		System.out.println("floatvariable=" + num5);
		System.out.println("doublevariable=" + num6);
		System.out.println("charvariable=" + grade);
		System.out.println("booleanvariable=" + pass);
        
	}

}
