package day7.loops;

public class DoWhileLoop3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        int n=10, times =5;
        
        do {
        	System.out.println("Java do while loops:" +n);
        	n++;
        }	while (n<=times);
        	
       n=10;
        while(n<=times) {
        	System.out.println("Java while loops:" +n);
        	n++;
        }
        
	}
      
}
